	if err = randomize.Struct(seed, &c, voteHistoryDBTypes, false, voteHistoryColumnsWithDefault...); err != nil {
		t.Fatal(err)
	}

	b.CandidateID = a.ID
	c.CandidateID = a.ID

	if err = b.Insert(ctx, tx, boil.Infer()); err != nil {
		t.Fatal(err)
	}
	if err = c.Insert(ctx, tx, boil.Infer()); err != nil {
		t.Fatal(err)
	}

	check, err := a.VoteHistories().All(ctx, tx)
	if err != nil {
		t.Fatal(err)
	}

	bFound, cFound := false, false
	for _, v := range check {
		if v.CandidateID == b.CandidateID {
			bFound = true
		}
		if v.CandidateID == c.CandidateID {
			cFound = true
		}
	}

	if !bFound {
		t.Error("expected to find b")
	}
	if !cFound {
		t.Error("expected to find c")
	}

	slice := CandidateSlice{&a}
	if err = a.L.LoadVoteHistories(ctx, tx, false, (*[]*Candidate)(&slice), nil); err != nil {
		t.Fatal(err)
	}
	if got := len(a.R.VoteHistories); got != 2 {
		t.Error("number of eager loaded records wrong, got:", got)
	}

	a.R.VoteHistories = nil
	if err = a.L.LoadVoteHistories(ctx, tx, true, &a, nil); err != nil {
		t.Fatal(err)
	}
	if got := len(a.R.VoteHistories); got != 2 {
		t.Error("number of eager loaded records wrong, got:", got)
	}

	if t.Failed() {
		t.Logf("%#v", check)
	}
