func testCandidateToManyAddOpVoteHistories(t *testing.T) {
	var err error

	ctx := context.Background()
	tx := MustTx(boil.BeginTx(ctx, nil))
	defer func() { _ = tx.Rollback() }()

	var a Candidate
	var b, c, d, e VoteHistory

	seed := randomize.NewSeed()
	if err = randomize.Struct(seed, &a, candidateDBTypes, false, strmangle.SetComplement(candidatePrimaryKeyColumns, candidateColumnsWithoutDefault)...); err != nil {
		t.Fatal(err)
	}
	foreigners := []*VoteHistory{&b, &c, &d, &e}
	for _, x := range foreigners {
		if err = randomize.Struct(seed, x, voteHistoryDBTypes, false, strmangle.SetComplement(voteHistoryPrimaryKeyColumns, voteHistoryColumnsWithoutDefault)...); err != nil {
			t.Fatal(err)
		}
	}

	if err := a.Insert(ctx, tx, boil.Infer()); err != nil {
		t.Fatal(err)
	}
	if err = b.Insert(ctx, tx, boil.Infer()); err != nil {
		t.Fatal(err)
	}
	if err = c.Insert(ctx, tx, boil.Infer()); err != nil {
		t.Fatal(err)
	}

	foreignersSplitByInsertion := [][]*VoteHistory{
		{&b, &c},
		{&d, &e},
	}

	for i, x := range foreignersSplitByInsertion {
		err = a.AddVoteHistories(ctx, tx, i != 0, x...)
		if err != nil {
			t.Fatal(err)
		}

		first := x[0]
		second := x[1]

		if a.ID != first.CandidateID {
			t.Error("foreign key was wrong value", a.ID, first.CandidateID)
		}
		if a.ID != second.CandidateID {
			t.Error("foreign key was wrong value", a.ID, second.CandidateID)
		}

		if first.R.Candidate != &a {
			t.Error("relationship was not added properly to the foreign slice")
		}
		if second.R.Candidate != &a {
			t.Error("relationship was not added properly to the foreign slice")
		}

		if a.R.VoteHistories[i*2] != first {
			t.Error("relationship struct slice not set to correct value")
		}
		if a.R.VoteHistories[i*2+1] != second {
			t.Error("relationship struct slice not set to correct value")
		}

		count, err := a.VoteHistories().Count(ctx, tx)
		if err != nil {
			t.Fatal(err)
		}
		if want := int64((i + 1) * 2); count != want {
			t.Error("want", want, "got", count)
		}
	}
}

